sys_variable_value.value:
var status = "Init";
var subject = "Simulation Subject";

var emailSearch = new GlideRecord('sn_sec_cmn_email_search');

if (emailSearch.get(workflow.inputs.email_search_id)) {
	workflow.scratchpad.queryStatus = "Found Email Search Record";

	var emailQuery = new GlideRecord('sn_sec_cmn_email_search_criteria');
	emailQuery.addQuery('email_search',emailSearch.sys_id);
	emailQuery.addQuery('search_field','subject');
	emailQuery.addQuery('search_text', '!=', '');
	emailQuery.query();
	
	if (emailQuery.next()) {
		workflow.scratchpad.queryStatus = "Found Email Search Query Record";
		var sedu = new x_snc_universal_dg.searchEmailDemoUtils();
		sedu.simulateSearch("${workflow.inputs.task_sys_id}", workflow.inputs.email_search_id, "Delete");
		workflow.scratchpad.queryStatus = "Search Simulation Complete";
	} else {
		workflow.scratchpad.queryStatus = "Found Search Record But No Subject Query";
	}
} else {
	workflow.scratchpad.queryStatus = "Cannot Find Parent Search";
}

gs.info('SECOPS Demo Utils: Email Search - ' + workflow.scratchpad.queryStatus);

