sys_variable_value.value:
var parentTask = new GlideRecord('task');
var targetCI = new GlideRecord('cmdb_ci');

if (workflow.variables.task_sys_id && workflow.variables.affected_ci) {

	if (parentTask.get(workflow.variables.task_sys_id)) {
		if (targetCI.get(workflow.variables.affected_ci)) {
			if (targetCI.name) {
				parentTask.work_notes = 'Successfully isolated host '+ targetCI.name 
					+' (UDG)';
				// FIXME: Should be automation_activity, but that's not working
			} else {
				parentTask.work_notes = 'Successfully isolated host (UDG)';
				// FIXME: Should be automation_activity, but that's not working
			}
			parentTask.update();
		} else {
			gs.warn('Warning: UDG - Isolate Host - failed to find CI by Sys ID ' + workflow.variables.affected_ci);
		}
	} else {
		
		gs.warn('Warning: UDG - Isolate Host - failed to find task by Sys ID ' + workflow.variables.task_sys_id);
	}
} else {
	gs.warn('Warning: UDG - Isolate Host - Called without Task and CI Sys IDs');
}

